  var secs = 30; // interwal w sekundach  
  var mins = 0;  // minutach
  var hours = 0; // godzinach
  var count = 2; // ile wiadomości/interwał
  var perTopic = true; // msg.topic/interwał (true), czy msg/interwał (false)
  //-----------------------------
  var message;

  (perTopic) ? message = msg.topic : message = "foo"
  secs = 1000*secs;
  mins = 60000*mins;
  hours = 3600000*hours;
  var interval = secs+mins+hours;

  if(!context.map){
    var map = new Map();
    context.map = map;
  }

  var assoc = context.map;
  var now = Date.now();

  if(assoc.get(message) === undefined){
    assoc.set(message, [now,2]);
    msg.debug1 = assoc.get(message)[1];
    return msg;
  } else {
      if(now - (assoc.get(message)[0]) > interval){
        assoc.get(message)[0] = now;
        assoc.get(message)[1] = 2;
        msg.debug2 = assoc.get(message)[1];
        return msg;
      }

       if( count >= assoc.get(message)[1] && (now - (assoc.get(message)[0]) < interval)){
         msg.debug3 = assoc.get(message)[1];
         assoc.get(message)[1] = assoc.get(message)[1] + 1;
         return msg;
       }    
return null;
}

